import type {GetStaticPropsContext, NextPage} from 'next';
import Head from 'next/head';
import {serverSideTranslations} from 'next-i18next/serverSideTranslations';
import {useTranslation} from 'next-i18next';
import Header from './components/Header';
import Footer from './components/Footer';
import AvatarEditor from './components/AvatarEditor';
import {useCallback, useEffect, useState} from "react";
import {INFURA_ID, NETWORK, NETWORKS} from "./common/constants";
import {useUserProviderAndSigner} from "eth-hooks";
import Web3Modal from "web3modal";
import WalletConnectProvider from "@walletconnect/web3-provider";
import Portis from "@portis/web3";
import Fortmatic from "fortmatic";

const {ethers} = require("ethers");

const scaffoldEthProvider = navigator.onLine
  ? new ethers.providers.StaticJsonRpcProvider("https://rpc.scaffoldeth.io:48544")
  : null;
const poktMainnetProvider = navigator.onLine
  ? new ethers.providers.StaticJsonRpcProvider(
    "https://eth-mainnet.gateway.pokt.network/v1/lb/611156b4a585a20035148406",
  )
  : null;
const mainnetInfura = navigator.onLine
  ? new ethers.providers.StaticJsonRpcProvider("https://mainnet.infura.io/v3/" + INFURA_ID)
  : null;

const mainnetProvider =
  poktMainnetProvider && poktMainnetProvider._isProvider
    ? poktMainnetProvider
    : scaffoldEthProvider && scaffoldEthProvider._network
    ? scaffoldEthProvider
    : mainnetInfura;
const targetNetwork = NETWORKS.mumbai; // <------- select your target frontend network (localhost, rinkeby, xdai, mainnet)
// ðŸ˜¬ Sorry for all the console logging
const DEBUG = true;
const NETWORKCHECK = true;

const web3Modal = new Web3Modal({
  network: "mainnet", // Optional. If using WalletConnect on xDai, change network to "xdai" and add RPC info below for xDai chain.
  cacheProvider: true, // optional
  theme: "light", // optional. Change to "dark" for a dark theme.
  providerOptions: {
    walletconnect: {
      package: WalletConnectProvider, // required
      options: {
        bridge: "https://polygon.bridge.walletconnect.org",
        infuraId: INFURA_ID,
        rpc: {
          1: `https://mainnet.infura.io/v3/${INFURA_ID}`, // mainnet // For more WalletConnect providers: https://docs.walletconnect.org/quick-start/dapps/web3-provider#required
          42: `https://kovan.infura.io/v3/${INFURA_ID}`,
          100: "https://dai.poa.network", // xDai
        },
      },
    },
    portis: {
      display: {
        logo: "https://user-images.githubusercontent.com/9419140/128913641-d025bc0c-e059-42de-a57b-422f196867ce.png",
        name: "Portis",
        description: "Connect to Portis App",
      },
      package: Portis,
      options: {
        id: "6255fb2b-58c8-433b-a2c9-62098c05ddc9",
      },
    },
    fortmatic: {
      package: Fortmatic, // required
      options: {
        key: "pk_live_5A7C91B2FC585A17", // required
      },
    },
    // "custom-walletlink": {
    //   display: {
    //     logo: "https://play-lh.googleusercontent.com/PjoJoG27miSglVBXoXrxBSLveV6e3EeBPpNY55aiUUBM9Q1RCETKCOqdOkX2ZydqVf0",
    //     name: "Coinbase",
    //     description: "Connect to Coinbase Wallet (not Coinbase App)",
    //   },
    //   package: walletLinkProvider,
    //   connector: async (provider, _options) => {
    //     await provider.enable();
    //     return provider;
    //   },
    // },
    // authereum: {
    //   package: Authereum, // required
    // },
  },
});


const Home: NextPage = () => {
  const {t} = useTranslation(`common`);

  const [address, setAddress] = useState();
  const [injectedProvider, setInjectedProvider] = useState();


  const localProviderUrl = targetNetwork.rpcUrl;
  // as you deploy to other networks you can set REACT_APP_PROVIDER=https://dai.poa.network in packages/react-app/.env
  const localProviderUrlFromEnv = process.env.REACT_APP_PROVIDER ? process.env.REACT_APP_PROVIDER : localProviderUrl;
  if (DEBUG) console.log("ðŸ  Connecting to provider:", localProviderUrlFromEnv);
  const localProvider = new ethers.providers.StaticJsonRpcProvider(localProviderUrlFromEnv);

  const userProviderAndSigner = useUserProviderAndSigner(injectedProvider, localProvider);
  // @ts-ignore
  const userSigner = userProviderAndSigner.signer;

  useEffect(() => {
    async function getAddress() {
      console.log("getAddress")
      if (userSigner) {
        const newAddress = await userSigner.getAddress();
        console.log(newAddress)
        // @ts-ignore
        setAddress(newAddress);
      }
    }

    getAddress();
  }, [userSigner]);


  const logoutOfWeb3Modal = async () => {
    await web3Modal.clearCachedProvider();
    // @ts-ignore
    if (injectedProvider && injectedProvider.provider && typeof injectedProvider.provider.disconnect == "function") {
      // @ts-ignore
      await injectedProvider.provider.disconnect();
    }
    setTimeout(() => {
      window.location.reload();
    }, 1);
  };

  const loadWeb3Modal = useCallback(async () => {
    const provider = await web3Modal.connect();
    setInjectedProvider(new ethers.providers.Web3Provider(provider));

    // @ts-ignore
    provider.on("chainChanged", chainId => {
      console.log(`chain changed to ${chainId}! updating providers`);
      setInjectedProvider(new ethers.providers.Web3Provider(provider));
    });

    provider.on("accountsChanged", () => {
      console.log(`account changed!`);
      setInjectedProvider(new ethers.providers.Web3Provider(provider));
    });

    // Subscribe to session disconnection
    // @ts-ignore
    provider.on("disconnect", (code, reason) => {
      console.log(code, reason);
      logoutOfWeb3Modal();
    });
  }, [setInjectedProvider]);

  useEffect(() => {
    if (web3Modal.cachedProvider) {
      loadWeb3Modal();
    }
  }, [loadWeb3Modal]);

  return (
    <>
      <Head>
        <link
          rel="apple-touch-icon"
          sizes="57x57"
          href="/favicon/apple-icon-57x57.png"
        />
        <link
          rel="apple-touch-icon"
          sizes="60x60"
          href="/favicon/apple-icon-60x60.png"
        />
        <link
          rel="apple-touch-icon"
          sizes="72x72"
          href="/favicon/apple-icon-72x72.png"
        />
        <link
          rel="apple-touch-icon"
          sizes="76x76"
          href="/favicon/apple-icon-76x76.png"
        />
        <link
          rel="apple-touch-icon"
          sizes="114x114"
          href="/favicon/apple-icon-114x114.png"
        />
        <link
          rel="apple-touch-icon"
          sizes="120x120"
          href="/favicon/apple-icon-120x120.png"
        />
        <link
          rel="apple-touch-icon"
          sizes="144x144"
          href="/favicon/apple-icon-144x144.png"
        />
        <link
          rel="apple-touch-icon"
          sizes="152x152"
          href="/favicon/apple-icon-152x152.png"
        />
        <link
          rel="apple-touch-icon"
          sizes="180x180"
          href="/favicon/apple-icon-180x180.png"
        />
        <link
          rel="icon"
          type="image/png"
          sizes="192x192"
          href="/favicon/android-icon-192x192.png"
        />
        <link
          rel="icon"
          type="image/png"
          sizes="32x32"
          href="/favicon/favicon-32x32.png"
        />
        <link
          rel="icon"
          type="image/png"
          sizes="96x96"
          href="/favicon/favicon-96x96.png"
        />
        <link
          rel="icon"
          type="image/png"
          sizes="16x16"
          href="/favicon/favicon-16x16.png"
        />
        <link rel="manifest" href="/manifest.json"/>
        <title>{t(`siteTitle`)}</title>
        <meta name="description" content={t(`siteDescription`)}/>
        <meta name="msapplication-TileColor" content="#fffefc"/>
        <meta
          name="msapplication-TileImage"
          content="/favicon/ms-icon-144x144.png"
        />
        <meta name="theme-color" content="#fffefc"/>
        <meta content={t(`siteDescription`)} name="description"/>
        <meta property="og:type" content="website"/>
        <meta property="og:site_name" content={t(`siteTitle`)}/>
        <meta property="og:title" content={t(`siteTitle`)}/>
        <meta property="og:description" content={t(`siteDescription`)}/>
        <meta property="og:image" content="https://i.imgur.com/F5R0K03.png"/>
        <meta name="twitter:card" content="summary_large_image"/>
        <meta name="twitter:image" content="https://i.imgur.com/F5R0K03.png"/>
        <meta name="twitter:site" content="@phillzou"/>
        <meta name="twitter:title" content={t(`siteTitle`)}/>
        <meta name="twitter:description" content={t(`siteDescription`)}/>
      </Head>

      <Header
        address={address}
        localProvider={localProvider}
        userSigner={userSigner}
        mainnetProvider={mainnetProvider}
        price={""}
        web3Modal={web3Modal}
        loadWeb3Modal={loadWeb3Modal}
        logoutOfWeb3Modal={logoutOfWeb3Modal}
        blockExplorer={"blockExplorer"}
      />
      <main className="my-5">
        <AvatarEditor/>
      </main>
      <Footer/>
    </>
  );
};

export async function getStaticProps({
                                       locale,
                                     }: GetStaticPropsContext & { locale: string }) {
  return {
    props: {
      ...(await serverSideTranslations(locale, [`common`])),
    },
  };
}

export default Home;
